import { useState } from "react";
// react component that copies the given text inside your clipboard
import { CopyToClipboard } from "react-copy-to-clipboard";
// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Collapse,
  Button,
  UncontrolledTooltip,
} from "reactstrap";
// core components
import HeadDesign from "components/Headers/HeadDesign.js";

import FileUpload from '../../../variables/FileUpload.js';
// import './Share.css';

// function App() {
const Share = () => {
  const [isOpen1, setIsOpen1] = useState(true);
  const [isOpen2, setIsOpen2] = useState(false);
  const [isOpen3, setIsOpen3] = useState(false);

  const toggleAccordion = (accordion) => {
    switch (accordion) {
      case 1:
        setIsOpen1(!isOpen1);
        break;
      case 2:
        setIsOpen2(!isOpen2);
        break;
      case 3:
        setIsOpen3(!isOpen3);
        break;
      default:
        break;
    }
  };
  return (
    <>
    <HeadDesign />
    <Container className="mt--7" fluid>
        {/* Table */}
        <Row>
          <div className="col">
            <Card className="shadow">
              <CardHeader className="bg-transparent">
                <h1 className="mb-0">NFT Minter</h1>
              </CardHeader>
              <CardBody>
                <h3 className="mb-0">Upload your file</h3>
                <p className="mb-0">
                  Upload your files to the IPFS network and mint your NFT.
                </p>


                {/* <h3 className="mb-0">Share your NFT</h3>
                <p className="mb-0">
                  Share your NFT with the world.
                </p> */}

                <FileUpload></FileUpload>
              </CardBody>
            </Card>
          </div>
        </Row>
      </Container>
    </>
  )
}

export default Share;
