import {useState} from "react";
// import '../assets/css/FileUpload.css';
// import MetaMaskButton from "./MetaMaskButton";
import {
  Row,
  Button,
  Card,
  CardBody,
  CardHeader,
  Collapse,
  Container,
} from "reactstrap";


const FileUpload=()=>{
const [file,setFile]=useState(null);
const [cid,setCid]=useState("");
const [transaction,setTransaction]=useState("");
const [isOpen1, setIsOpen1] = useState(true);
const [isOpen2, setIsOpen2] = useState(false);
const [isOpen3, setIsOpen3] = useState(false);

const toggleAccordion = (accordion) => {
  switch (accordion) {
    case 1:
      setIsOpen1(!isOpen1);
      break;
    case 2:
      setIsOpen2(!isOpen2);
      break;
    case 3:
      setIsOpen3(!isOpen3);
      break;
    default:
      break;
  }
};

    const handleSubmit =async(event)=>{
        event.preventDefault();
         try{
             if(file){
                 const formData = new FormData();
                 formData.append("file",file);
                 const response = await fetch('http://localhost:5000/upload',{
                     method:'POST',
                     body:formData
                 }).then(response=>response.json())
                 .then(data=>{ 
                    setCid(data.cid);
                    setTransaction(data.transactionHash)
                   console.log(data.cid)
                   console.log(data.transactionHash)
                 })
                 .catch(error=>{
                     console.error(error);
                 })
             }
         }catch(error){
            alert(error);
         }
    }
    const retreieveFile=(event)=>{
        try{
            const data = event.target.files[0];
            setFile(data);
            event.preventDefault();
        }catch(error){
            alert("Retrieve File Does Not Worked");
        }
    }

return<>

    <div className="img-ctr">
        {cid && <a href={`https://${cid}.ipfs.dweb.link`}><img src={`https://${cid}.ipfs.dweb.link`} height={"250px"} /></a>}
        </div>
        <div className="transaction">
        {transaction && <a href={`https://mumbai.polygonscan.com/tx/${transaction}`} target="_blank">Transaction Detials</a>}
    </div>
    <div className="form">

        <Container className="mt--7" fluid>
            {/* Table */}
            <Row>
                <div className="form">
                  <Button
                    max-width="100%"
                    color="primary"
                    onClick={() => toggleAccordion(1)}
                    style={{ marginBottom: "1rem" }}
                  >
                    Accordion Item #1
                  </Button>
                  <Collapse isOpen={isOpen1}>
                    <Card>
                      <CardHeader>Document Type</CardHeader>
                      <CardBody>
                        <strong>This is the first item's accordion body.</strong> It is shown by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables.
                      </CardBody>
                    </Card>
                  </Collapse>

                  <Button
                    color="primary"
                    onClick={() => toggleAccordion(2)}
                    style={{ marginBottom: "1rem" }}
                  >
                    Accordion Item #2
                  </Button>
                  <Collapse isOpen={isOpen2}>
                    <Card>
                      <CardHeader>Accordion Item #2</CardHeader>
                      <CardBody>
                        <strong>This is the second item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables.
                      </CardBody>
                    </Card>
                  </Collapse>

                  <Button
                    color="primary"
                    onClick={() => toggleAccordion(3)}
                    style={{ marginBottom: "1rem" }}
                  >
                    Accordion Item #3
                  </Button>
                  <Collapse isOpen={isOpen3}>
                    <Card>
                      <CardHeader>Accordion Item #3</CardHeader>
                      <CardBody>
                        <strong>This is the third item's accordion body.</strong> It is hidden by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables.
                      </CardBody>
                    </Card>
                  </Collapse>

                </div>
            </Row>
        </Container>

        <form onSubmit={handleSubmit}>

            <input type="file" className="choose" onChange={retreieveFile}/>
            <button className="btn" style={{backgroundColor:'#2ECC71',color:"white"}}>NFt Minter</button>

        </form>
        {/* <MetaMaskButton /> */}
    </div>
</>
}
export default FileUpload;
