  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  UncontrolledTooltip,
} from "reactstrap";
// core components
import HeadDesign from "components/Headers/HeadDesign.js";

import FileUpload from './FileUpload';
// import './Share.css';

